  loadBlob (blob) {
    return fetch(`/assets/blobs/${blob}.md`)
           .then(response => response.text())
           .then(markdown => marked(markdown, {}))
  }

  componentDidMount () {
  }

  componentWillAppear (callback) {
    callback()
  }

  componentDidAppear () {
  }

